mapper.update:
update dm_user
set password = #{password},update_time = now()
where username = #{username}

mapper.select:
select

from dm_user
where username = #{username}
and password = #{password}

select count(1) from dm_user
where username = #{username}
and phone = #{phone}

select count(1) from dm_user
where password = #{oldPassword}
and id = #{id}

select count(1) from dm_user
where  email = #{email}
and id != #{id}
